package com.example.gread.gread;import android.content.Intent;import android.graphics.Typeface;import android.os.Bundle;import android.support.v4.app.FragmentActivity;import android.view.View;import android.view.Window;import android.view.WindowManager;import android.widget.Button;import android.widget.EditText;import com.mongodb.MongoClient;import com.mongodb.client.MongoCollection;import com.mongodb.client.MongoDatabase;import org.bson.Document;//import com.facebook.CallbackManager;//import com.facebook.FacebookSdk;//import com.facebook.appevents.AppEventsLogger;//import com.facebook.login.widget.LoginButton;public class MainActivity extends FragmentActivity{    EditText userName, password;    protected void onCreate(Bundle savedInstanceState)    {        super.onCreate(savedInstanceState);        requestWindowFeature(Window.FEATURE_NO_TITLE);        this.getWindow().setFlags(WindowManager.LayoutParams.FLAG_FULLSCREEN,                WindowManager.LayoutParams.FLAG_FULLSCREEN);        setContentView(R.layout.activity_main);        MongoClient mc= new MongoClient();        MongoDatabase mdb=mc.getDatabase("local");        MongoCollection<Document> collection=mdb.getCollection("users");        Document doc=new Document("name","shubh");        collection.insertOne(doc);        Button register = (Button) findViewById(R.id.register_button_login);        Typeface register_font= Typeface.createFromAsset(getAssets(), "fonts/Port.ttf");        register.setTypeface(register_font);        register.setOnClickListener(new View.OnClickListener()        {            public void onClick(View v)            {                Intent sub = new Intent(MainActivity.this, RegisterActivity.class);                startActivity(sub);                System.out.println("Done");                // Perform action on click            }        });}    public void validateLogin(View view)    {        userName = (EditText)findViewById(R.id.username_login);        password=(EditText)findViewById(R.id.password_login);        String uname=userName.getText().toString();        String pass=password.getText().toString();        if(uname.equals("shubh") && pass.equals("shubh"))        {            Intent loginIntent = new Intent(this, HomeActivity.class);            startActivity(loginIntent);            //System.out.println("Done");        }        else            MainActivity.super.onRestart();    }}